local Players = game:GetService("Players") local Workspace = game:GetService("Workspace") local ReplicatedStorage = game:GetService("ReplicatedStorage") local TweenService = game:GetService("TweenService")  local LocalPlayer = Players.LocalPlayer  local ScreenGui = Instance.new("ScreenGui") ScreenGui.Name = "SilentHub" ScreenGui.ResetOnSpawn = false ScreenGui.IgnoreGuiInset = true ScreenGui.Parent = LocalPlayer:WaitForChild("PlayerGui")  local Frame = Instance.new("Frame") Frame.Size = UDim2.new(0, 270, 0, 150) Frame.Position = UDim2.new(0.35, 0, 0.35, 0) Frame.BackgroundColor3 = Color3.fromRGB(10, 0, 20) Frame.BorderSizePixel = 0 Frame.Active = true Frame.Draggable = true Frame.ClipsDescendants = true Frame.Parent = ScreenGui  local UICorner = Instance.new("UICorner", Frame) UICorner.CornerRadius = UDim.new(0, 14)  local function createGlow(parent, sizeScale, transparency) 	local glow = Instance.new("ImageLabel") 	glow.AnchorPoint = Vector2.new(0.5, 0.5) 	glow.Position = UDim2.new(0.5, 0, 0.5, 0) 	glow.Size = UDim2.new(sizeScale, sizeScale, sizeScale, sizeScale) 	glow.BackgroundTransparency = 1 	glow.Image = "rbxassetid://5028857084" 	glow.ImageColor3 = Color3.fromRGB(180, 0, 255) 	glow.ImageTransparency = transparency 	glow.ScaleType = Enum.ScaleType.Slice 	glow.SliceCenter = Rect.new(24, 24, 276, 276) 	glow.ZIndex = 0 	glow.Parent = parent 	return glow end  local glowBig = createGlow(Frame, 1.8, 0.55) local glowMedium = createGlow(Frame, 1.5, 0.45) local glowSmall = createGlow(Frame, 1.2, 0.35)  local Title = Instance.new("TextLabel", Frame) Title.Text = "SilentHub üíÄ" Title.Size = UDim2.new(1, 0, 0, 40) Title.BackgroundColor3 = Color3.fromRGB(25, 0, 50) Title.BorderSizePixel = 0 Title.Font = Enum.Font.GothamBlack Title.TextColor3 = Color3.fromRGB(200, 0, 255) Title.TextStrokeTransparency = 0.8 Title.TextStrokeColor3 = Color3.fromRGB(255, 255, 255) Title.TextSize = 20  local TitleCorner = Instance.new("UICorner", Title) TitleCorner.CornerRadius = UDim.new(0, 14)  local Underline = Instance.new("Frame", Frame) Underline.Size = UDim2.new(0.8, 0, 0, 2) Underline.Position = UDim2.new(0.1, 0, 0, 38) Underline.BackgroundColor3 = Color3.fromRGB(150, 0, 255) Underline.BorderSizePixel = 0  local Button = Instance.new("TextButton", Frame) Button.Size = UDim2.new(0.85, 0, 0, 60) Button.Position = UDim2.new(0.075, 0, 0.5, -5) Button.BackgroundColor3 = Color3.fromRGB(40, 0, 80) Button.Text = "Disabled ‚ùå" Button.TextColor3 = Color3.fromRGB(255, 255, 255) Button.Font = Enum.Font.GothamBold Button.TextSize = 20  local BtnCorner = Instance.new("UICorner", Button) BtnCorner.CornerRadius = UDim.new(0, 10)  local UIStroke = Instance.new("UIStroke", Button) UIStroke.Thickness = 2 UIStroke.Color = Color3.fromRGB(180, 0, 255) UIStroke.Transparency = 0.6  task.spawn(function() 	while true do 		local tweenInfo = TweenInfo.new(2, Enum.EasingStyle.Sine, Enum.EasingDirection.InOut) 		TweenService:Create(glowBig, tweenInfo, {ImageTransparency = 0.2, ImageColor3 = Color3.fromRGB(220,0,255)}):Play() 		TweenService:Create(glowMedium, tweenInfo, {ImageTransparency = 0.1, ImageColor3 = Color3.fromRGB(200,0,255)}):Play() 		TweenService:Create(glowSmall, tweenInfo, {ImageTransparency = 0.05, ImageColor3 = Color3.fromRGB(180,0,255)}):Play() 		TweenService:Create(Button, tweenInfo, {BackgroundColor3 = Color3.fromRGB(80, 0, 160)}):Play() 		task.wait(2)  		TweenService:Create(glowBig, tweenInfo, {ImageTransparency = 0.55, ImageColor3 = Color3.fromRGB(140,0,255)}):Play() 		TweenService:Create(glowMedium, tweenInfo, {ImageTransparency = 0.45, ImageColor3 = Color3.fromRGB(120,0,255)}):Play() 		TweenService:Create(glowSmall, tweenInfo, {ImageTransparency = 0.35, ImageColor3 = Color3.fromRGB(100,0,255)}):Play() 		TweenService:Create(Button, tweenInfo, {BackgroundColor3 = Color3.fromRGB(40, 0, 80)}):Play() 		task.wait(2) 	end end)  local sentryActive = false local sentryLoop  local function teleportAllSentriesToFront() 	local hrp = LocalPlayer.Character and LocalPlayer.Character:FindFirstChild("HumanoidRootPart") 	if not hrp then return end  	for _, player in ipairs(Players:GetPlayers()) do 		if player ~= LocalPlayer then 			local sentryName = "Sentry_" .. tostring(player.UserId) 			local sentry = Workspace:FindFirstChild(sentryName)  			if not sentry then 				local template = ReplicatedStorage:FindFirstChild(sentryName) 				if template then 					sentry = template:Clone() 					sentry.Parent = Workspace 				end 			end  			if sentry then 				local offset = hrp.CFrame.LookVector * 3 + Vector3.new(0, 2, 0) 				sentry.CFrame = CFrame.new(hrp.Position + offset) 				sentry.CanCollide = true 			end 		end 	end end  local function startSentryLoop() 	if sentryLoop then return end 	sentryLoop = task.spawn(function() 		while sentryActive do 			teleportAllSentriesToFront() 			task.wait(1) 		end 	end) end  local function stopSentryLoop() 	sentryActive = false 	sentryLoop = nil end  local function setButtonState(active) 	local onColor = Color3.fromRGB(150, 0, 255) 	local offColor = Color3.fromRGB(40, 0, 80) 	local tweenTime = 0.25  	if active then 		Button.Text = "Enabled ‚úÖ" 		TweenService:Create(Button, TweenInfo.new(tweenTime), {BackgroundColor3 = onColor}):Play() 	else 		Button.Text = "Disabled ‚ùå" 		TweenService:Create(Button, TweenInfo.new(tweenTime), {BackgroundColor3 = offColor}):Play() 	end end  Button.MouseButton1Click:Connect(function() 	sentryActive = not sentryActive 	if sentryActive then 		startSentryLoop() 	else 		stopSentryLoop() 	end 	setButtonState(sentryActive) end)
